INSERT INTO `creature_ai_texts` VALUES ('-1106', 'Long live the Dragonmaw! Die you worthless $R!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', 'Text');
INSERT INTO `creature_ai_texts` VALUES ('-1107', 'Never cross a Dark Iron, $C.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '1169');
INSERT INTO `creature_ai_texts` VALUES ('-1108', 'Time to join us, $C.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '1157');
INSERT INTO `creature_ai_texts` VALUES ('-1109', 'Brains...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '1158');
INSERT INTO `creature_ai_texts` VALUES ('-1110', 'Mok-kha Khazrega ta Thulmane', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '1364');
INSERT INTO `creature_ai_texts` VALUES ('-1111', 'Your bones will break under my boot, $R!', null, null, null, null, null, null, null, null, '0', '0', '1', '0', '2108');
INSERT INTO `creature_ai_texts` VALUES ('-1112', 'USURPERS!!! GNOMEREGAN IS MINE!!!', null, null, null, null, null, null, null, null, '5807', '1', '0', '0', '7800');
INSERT INTO `creature_ai_texts` VALUES ('-1113', 'My machines are the future! They\'ll destroy you all!', null, null, null, null, null, null, null, null, '5808', '1', '0', '0', '7800');
INSERT INTO `creature_ai_texts` VALUES ('-1114', '...and stay dead! He got served!', null, null, null, null, null, null, null, null, '5810', '1', '0', '0', '7800');
INSERT INTO `creature_ai_texts` VALUES ('-1115', 'You have faced many challenges, pity they were all in vain. Soon your people will kneel to my lord!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1116', 'Mine! Mine! Mine! Gizlock is the ruler of this domain! You shall never reveal my presence!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '13601');
INSERT INTO `creature_ai_texts` VALUES ('-1117', 'What? Oh no. I don\'t care what you have to say. I just enjoy inflicting pain.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '4306');
INSERT INTO `creature_ai_texts` VALUES ('-1118', 'You will talk eventually. You might as well spill it now.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4306');
INSERT INTO `creature_ai_texts` VALUES ('-1119', 'Confess and we shall set you free.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '4306');
INSERT INTO `creature_ai_texts` VALUES ('-1120', 'We are betrayed!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '10433');
INSERT INTO `creature_ai_texts` VALUES ('-1121', 'lets out a shrill cry.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '10506');
INSERT INTO `creature_ai_texts` VALUES ('-1122', 'Hmmm, this one looks like something that would be better off as a windchime. Take notes class... This is NOT what you want to summon in the heat of battle.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '11582');
INSERT INTO `creature_ai_texts` VALUES ('-1123', 'This one is slightly better than the last. However, it still suffers from the same flimsy bone structure as the others. When you summon one of these, you are on the right path.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '11582');
INSERT INTO `creature_ai_texts` VALUES ('-1124', 'Now this is an example of what not to summon. Look, it\'s frail and brittle. One good whack from a mace will send bone shards flying.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '11582');
INSERT INTO `creature_ai_texts` VALUES ('-1125', 'Why haven\'t the Stormwind guards come?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '325');
INSERT INTO `creature_ai_texts` VALUES ('-1126', 'The coils of death... Will crush you.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3670');
INSERT INTO `creature_ai_texts` VALUES ('-1127', 'You will never wake the dreamer!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3669');
INSERT INTO `creature_ai_texts` VALUES ('-1128', 'I am the serpent king! I can do anything.', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3673');
INSERT INTO `creature_ai_texts` VALUES ('-1129', 'I\'m going to wear your skin as a smoking jacket!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '11058');
INSERT INTO `creature_ai_texts` VALUES ('-1130', 'The stogies? You\'ll have to pry them from my cold dead... er... RAWR!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '11058');
INSERT INTO `creature_ai_texts` VALUES ('-1131', 'The living are here!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '10411');
INSERT INTO `creature_ai_texts` VALUES ('-1132', 'None may challenge the Brotherhood!', null, null, null, null, null, null, null, null, '5780', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-1133', 'Lap dogs, all of you!', null, null, null, null, null, null, null, null, '5782', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-1134', 'calls more of his allies out of the shadows.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-1135', 'Fools! our cause it righteous!', null, null, null, null, null, null, null, null, '5783', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-1136', 'and stay down!', null, null, null, null, null, null, null, null, '5781', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-1137', 'The Brotherhood will prevail!', null, null, null, null, null, null, null, null, '5784', '1', '0', '0', '639');
INSERT INTO `creature_ai_texts` VALUES ('-1138', 'VanCleef pay big for your heads!', null, null, null, null, null, null, null, null, '5774', '1', '0', '0', '644');
INSERT INTO `creature_ai_texts` VALUES ('-1139', 'We\'re under attack! Avast ya swabs! Repel the invaders!', null, null, null, null, null, null, null, null, '5777', '1', '0', '0', '646');
INSERT INTO `creature_ai_texts` VALUES ('-1140', 'You land lovers are tougher then I thought! I will need to improvise', null, null, null, null, null, null, null, null, '5778', '0', '0', '0', '646');
INSERT INTO `creature_ai_texts` VALUES ('-1141', 'D\'ah! Now you\'re making me angry!', null, null, null, null, null, null, null, null, '5779', '0', '0', '0', '646');
INSERT INTO `creature_ai_texts` VALUES ('-1142', 'Anyone want to take a break?  Well too bad!  Get to work you oafs!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '1763');
INSERT INTO `creature_ai_texts` VALUES ('-1143', 'Get those parts moving down to the ship!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '1763');
INSERT INTO `creature_ai_texts` VALUES ('-1144', 'jumps out of the shadows!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '636');
INSERT INTO `creature_ai_texts` VALUES ('-1145', 'My lord will be the end you all...', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1146', 'Is there no one left to test me?!?', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1147', 'Your days are done!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1148', 'Your time is running out!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1149', 'The Dark Lord laughs at you!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1150', 'You are nothing, I answer a higher call!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1151', 'You have faced many challenges, pity they were all in vain. Soon your people will kneel to my lord!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '17537');
INSERT INTO `creature_ai_texts` VALUES ('-1152', 'Lok Narash!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17370');
INSERT INTO `creature_ai_texts` VALUES ('-1153', 'The blood is our power!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17491');
INSERT INTO `creature_ai_texts` VALUES ('-1154', 'For Kargath! For Victory!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17397');
INSERT INTO `creature_ai_texts` VALUES ('-1155', 'Lok\'tar Illidari!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17397');
INSERT INTO `creature_ai_texts` VALUES ('-1156', 'This world is OURS!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17397');
INSERT INTO `creature_ai_texts` VALUES ('-1157', 'calls for his worgs!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '19442');
INSERT INTO `creature_ai_texts` VALUES ('-1158', 'examines the information on the wanted poster.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '18098');
INSERT INTO `creature_ai_texts` VALUES ('-1159', 'whispers softle in reverent tones under her breath.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '21066');
INSERT INTO `creature_ai_texts` VALUES ('-1160', 'I wanna be just like the pitfighter!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18292');
INSERT INTO `creature_ai_texts` VALUES ('-1161', 'Will he win? Can he win?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18292');
INSERT INTO `creature_ai_texts` VALUES ('-1162', 'He\'s so well diciplined!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18293');
INSERT INTO `creature_ai_texts` VALUES ('-1163', 'How does he do it?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18296');
INSERT INTO `creature_ai_texts` VALUES ('-1164', 'Wow! Look at his muscles!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18296');
INSERT INTO `creature_ai_texts` VALUES ('-1165', 'Damm those ogres. Damn them to the Nether. Won\'t someone save us from these savages! This is the fifth kidnapping is as many days.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18407');
INSERT INTO `creature_ai_texts` VALUES ('-1166', 'That\'s the best reward we\'re going to be able to offer. Hopefully someone accepts the mission.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '18407');
INSERT INTO `creature_ai_texts` VALUES ('-1167', 'If my stomach wasn\'t threatening to dislodge itself from my body and leave me for the refuse that we give to the prisoners, I might be insulted by this... this food.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16590');
INSERT INTO `creature_ai_texts` VALUES ('-1168', 'Do I detect a hint of cinnamon? Or is that nutmeg? Whatever it is, one thing is for sure - nobody cooks donkey like you, Cookie.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16590');
INSERT INTO `creature_ai_texts` VALUES ('-1169', 'What kinda orcs, chief?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16590');
INSERT INTO `creature_ai_texts` VALUES ('-1170', 'You mean...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16590');
INSERT INTO `creature_ai_texts` VALUES ('-1171', 'But how, chief? I thought the curse was lifted...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16590');
INSERT INTO `creature_ai_texts` VALUES ('-1172', 'Yer slop is ready. Come n\' get it...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16585');
INSERT INTO `creature_ai_texts` VALUES ('-1173', 'And if I hear that you palookas complained to Nazgrel again, it\'ll be one of ya\'s that I serve up next time...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16585');
INSERT INTO `creature_ai_texts` VALUES ('-1174', 'I\'ve seen gnomes work harder than you, peon! Little girl gnomes - with pig tails! MOVE IT, MOVE IT, MOVE IT!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16584');
INSERT INTO `creature_ai_texts` VALUES ('-1175', 'Yea, we got problems. I think that\'s crystal clear. The question is, what are we gonna do about it?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19255');
INSERT INTO `creature_ai_texts` VALUES ('-1176', 'The warchief\'s put a price on Arazzius\'s head! As of right now, that no-good piece of demon refuse is enemy number one.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19255');
INSERT INTO `creature_ai_texts` VALUES ('-1177', 'So there we were - me and the battalion, doing a routine recon down the bone road. We\'d sent the scout up ahead to look around in case anything dangerous come up.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1178', 'Sure enough, scount came runnin\' back, talkin\' bout some orcs down the path. They hadn\'t seen us yet so we figured we\'d go get aquainted.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1179', 'Big red bastards! Bigger than any orc I\'d ever seen. Muscles out to here, tusks out to here...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1180', 'nods', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1181', 'Fel orcs...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1182', 'I dunno, grunt. I just - don\'t - know...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1183', 'But that\'s not the worst of it. Before I could pull us back, a group of \'em spotted us and attacked.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1184', 'Lost some of my best soldiers that day...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '16589');
INSERT INTO `creature_ai_texts` VALUES ('-1185', 'I feel that war is fast approaching. The fruit vendor has enlisted to help an ogre. I shall have to enlist help as well.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19196');
INSERT INTO `creature_ai_texts` VALUES ('-1186', 'FRUIT VENDOR!!! Your cart is still in our way! We will give you one more hour to move it from our area. Do not test our patience anymore!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '19196');
INSERT INTO `creature_ai_texts` VALUES ('-1187', 'I could have sworn I had more apples. Grok!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19223');
INSERT INTO `creature_ai_texts` VALUES ('-1188', 'What is that poor orc yelling about? Someone should see what is going on.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19223');
INSERT INTO `creature_ai_texts` VALUES ('-1189', 'C\'mere, $r. Ya don\'t want to be riskin\' yerself in a dungeon, do ya? Filthy work, and for what? Some worthless trinket? Nah, what ya need be one of my amulets. Guarenteed to work!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1190', 'Hey! Hey, $r! I be havin\' just the thing for ya. Ya ever see a Tikbalang? Frightenin\' creatures. Prone to stealin\' ya away. What ya need is my tikbaland wards. Guaranteed to ward away all tikbalangs!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1191', 'Look over here, $c! I got somethin\' for ya. Just what ya need. It\'s soap, right... and it\'s on a rope! No offense, man, but yer a little ripe, you know what I\'m sayin\'? ', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1192', 'Ya look tired, $c. I be havin\' somethin\' for ya. These amulets, they can help ya leap obstacles and swim through the rivers like ya be born to it. They energize the food ya be eatin\'. Makin\' yer wounds close before yer eyes!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1193', 'Amazing Amulets! Incredible curios! The newfangled jewlcrafters be havin\' nothin\' on the tried and true mystical methods of ol\' Griftah! Improve yerself through these magical talismans for a bargain price!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1194', 'Ya look lovesick. Special someone be gettin\' ya down, eh? No worries, no worries. Ya get this medallion from me, ya wear it when ya see \'em and they be all over ya, sweetheart!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1195', 'Dangerous out there, innit? Know what ya need? Protection. I can help ya defy death itself! Ya wear this madstone \'round yer neck, see, and then ya be able to come back if ya find yerself in the spirit world! Just a quick jaunt to yer corpse and up ya com', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19227');
INSERT INTO `creature_ai_texts` VALUES ('-1196', 'Nothing like a tankard full to wash down one of my magnificent muffins.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19664');
INSERT INTO `creature_ai_texts` VALUES ('-1197', 'Yea, that hit the spot. Muffins for sale! Get your fresh muffins right here!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19664');
INSERT INTO `creature_ai_texts` VALUES ('-1198', 'Yea, yea, I\'m the muffin man. You know why? Because I sell muffins, that\'s why!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19664');
INSERT INTO `creature_ai_texts` VALUES ('-1199', 'I\'ve got muffins right over here. Come on over!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19664');
INSERT INTO `creature_ai_texts` VALUES ('-1200', 'If no one help. Grok smash!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22940');
INSERT INTO `creature_ai_texts` VALUES ('-1201', 'Grok need help! Why no one help?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22940');
INSERT INTO `creature_ai_texts` VALUES ('-1202', 'Mog\'dorg the Wizened tell Grok go to city, get help for Bloodmaul. Grok go.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22940');
INSERT INTO `creature_ai_texts` VALUES ('-1203', 'Bladespire clan not fight fair. Have Gronn to help dem. You go to Blade\'s Edge Mountains. Talk to Mog\'dorg the Wizened. Kill Gromm', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22940');
INSERT INTO `creature_ai_texts` VALUES ('-1204', 'Grok get angry if puny ones not go to Blade\'s Edge and talk to Mog\'dorg!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22940');
INSERT INTO `creature_ai_texts` VALUES ('-1205', 'Me hungry. Fruit look tasty.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22940');
INSERT INTO `creature_ai_texts` VALUES ('-1206', 'Anchorite Nindumen, I have a request to make of you.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19346');
INSERT INTO `creature_ai_texts` VALUES ('-1207', 'Of course, my friend. How can I be of service to you?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1208', 'We\'ve just sent another inexperienced squad into Nagrand. Might you offer a prayer for them?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19346');
INSERT INTO `creature_ai_texts` VALUES ('-1209', 'A noble request. It would be an honor, Erothem.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1210', 'My friends, please join me in humble supplication the the Light.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1211', 'Light that we embrace, we beseech thee...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1212', 'Into our struggle against the darkness we have sent our children.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1213', 'Please bless and protect them, and grant them success over those who seek to do us harm...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1214', 'Continue to bless this, our ancient home, we beg.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1215', 'Bless us with tolerance for our elven visitors.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1216', 'By tha Naaru, may it be so.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19378');
INSERT INTO `creature_ai_texts` VALUES ('-1217', 'May it be so.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19346');
INSERT INTO `creature_ai_texts` VALUES ('-1218', 'May it be so.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19138');
INSERT INTO `creature_ai_texts` VALUES ('-1219', 'Last time someone went out to try and collect the bounty on Netherlock, all we got back was a compressed layer of foolhardy adventurer!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19541');
INSERT INTO `creature_ai_texts` VALUES ('-1220', 'Maybe I should gather up some other agents and head out there after this shift?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19541');
INSERT INTO `creature_ai_texts` VALUES ('-1221', 'The creatures of this place are strong, wayfarer. You\'ll need ample ammunition if you hope to survive. You\'re in luck - I am well-stocked with fine arrows, bullets, and throwing weapons. The price is reasonable, considering the circumstances.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19534');
INSERT INTO `creature_ai_texts` VALUES ('-1222', 'Ahh, another powerful traveler. The nether can be dangerous; you\'ll want the most potent spells at your disposal. I carry the finest reagents, common and exotic. Come and buy - you won\'t be sorry!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19535');
INSERT INTO `creature_ai_texts` VALUES ('-1223', 'You will never wake the dreamer!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', '3669');
INSERT INTO `creature_ai_texts` VALUES ('-1224', 'bares her fangs and hisses menacingly at those around her.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '23163');
INSERT INTO `creature_ai_texts` VALUES ('-1225', 'I see those fools sent some fresh meat for us.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '103');
INSERT INTO `creature_ai_texts` VALUES ('-1226', 'You dare to disturb the loyal followers of the Raven?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22993');
INSERT INTO `creature_ai_texts` VALUES ('-1227', 'You will die for your hubris, druid!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22993');
INSERT INTO `creature_ai_texts` VALUES ('-1228', 'You dare to disturb the loyal followers of the Raven?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22994');
