INSERT INTO `creature_ai_texts` VALUES ('-1231', 'The time of the Raven is night, but yours will end here!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '22992');
INSERT INTO `creature_ai_texts` VALUES ('-1232', 'Furious winds envelop Vakkiz as he writhes menacingly towards you.', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '23162');
INSERT INTO `creature_ai_texts` VALUES ('-1233', '\'s flesh shatters in an unearthly rage!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '23162');
INSERT INTO `creature_ai_texts` VALUES ('-1234', 'Someone come read this wanted poster to Grek. Grek can\'t read.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19606');
INSERT INTO `creature_ai_texts` VALUES ('-1235', 'Grek try!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19606');
INSERT INTO `creature_ai_texts` VALUES ('-1236', 'This oil no good for Grek! What Grek look like to you, some weakling in robes?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19606');
INSERT INTO `creature_ai_texts` VALUES ('-1237', 'Grek get a drink', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19606');
