INSERT INTO `creature_ai_texts` VALUES ('-1247', 'Any time any of you peons want to take a break, the graveyard is right over there.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19362');
INSERT INTO `creature_ai_texts` VALUES ('-1248', 'What is it that you want from me, shaman?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '21769');
INSERT INTO `creature_ai_texts` VALUES ('-1249', 'Do you not have eyes? Did you not see the demons on our very doorstep? I do not have the soldiers to spare!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '21769');
INSERT INTO `creature_ai_texts` VALUES ('-1250', 'Pray to your gods, shaman. Perhaps they will come to your aid. The Horde however, cannot...', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '21769');
INSERT INTO `creature_ai_texts` VALUES ('-1251', 'Wit a reward like dis, how could they refuse, mon?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '21771');
INSERT INTO `creature_ai_texts` VALUES ('-1252', 'Bron! \'Ey Bron! \'Ey Bron! Hit me!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19394');
INSERT INTO `creature_ai_texts` VALUES ('-1253', 'Nae, that\'s fer wimps. Hit me with yer hammer!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19394');
INSERT INTO `creature_ai_texts` VALUES ('-1254', 'DO IT, YE PANSEY!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19394');
INSERT INTO `creature_ai_texts` VALUES ('-1255', '...THAT WAS GREAT!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '19394');
INSERT INTO `creature_ai_texts` VALUES ('-1256', 'I don\'t get it! Are ya blind? Can ya not see the demons bangin\' on our door? Who am I gonna send out to talk to your shaman friends? Point \'em out!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '21773');
