INSERT INTO `creature_ai_texts` VALUES ('-1259', 'This vessel... is empty', null, null, null, null, null, null, null, null, '11251', '1', '0', '0', '20870');
INSERT INTO `creature_ai_texts` VALUES ('-1260', 'No...more...life', null, null, null, null, null, null, null, null, '11252', '1', '0', '0', '20870');
