INSERT INTO `creature_ai_texts` VALUES ('-1284', 'I hate to say I told you so...', null, null, null, null, null, null, null, null, '11207', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1285', 'You won\'t... get far', null, null, null, null, null, null, null, null, '11212', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1286', 'I revel in your pain!', null, null, null, null, null, null, null, null, '11209', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1287', 'You seek a prize, eh? How about death?', null, null, null, null, null, null, null, null, '11206', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1288', 'Your life will be mine!', null, null, null, null, null, null, null, null, '11208', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1289', 'I\'ll incinerate you!', null, null, null, null, null, null, null, null, '11210', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1290', 'Scream while you burn! ', null, null, null, null, null, null, null, null, '11211', '1', '0', '0', '17978');
INSERT INTO `creature_ai_texts` VALUES ('-1291', 'I predict a painful death.', null, null, null, null, null, null, null, null, '11101', '1', '0', '0', '19218');
INSERT INTO `creature_ai_texts` VALUES ('-1292', 'Your strategy was flawed.', null, null, null, null, null, null, null, null, '11102', '1', '0', '0', '19218');
INSERT INTO `creature_ai_texts` VALUES ('-1293', 'Yes, the only logical outcome.', null, null, null, null, null, null, null, null, '11103', '0', '0', '0', '19218');
INSERT INTO `creature_ai_texts` VALUES ('-1294', 'An unforeseen... contingency.', null, null, null, null, null, null, null, null, '11106', '1', '0', '0', '19218');
INSERT INTO `creature_ai_texts` VALUES ('-1295', 'Measure twice cut once.', null, null, null, null, null, null, null, null, '11104', '1', '0', '0', '19218');
INSERT INTO `creature_ai_texts` VALUES ('-1296', 'If my division is correct you should be quite dead.', null, null, null, null, null, null, null, null, '11105', '1', '0', '0', '19218');
INSERT INTO `creature_ai_texts` VALUES ('-1297', 'You should split while you can.', null, null, null, null, null, null, null, null, '11162', '1', '0', '0', '19219');
INSERT INTO `creature_ai_texts` VALUES ('-1298', 'Damn, I\'m good!', null, null, null, null, null, null, null, null, '11164', '1', '0', '0', '19219');
INSERT INTO `creature_ai_texts` VALUES ('-1299', 'Can\'t say I didn\'t warn you!', null, null, null, null, null, null, null, null, '11163', '1', '0', '0', '19219');
INSERT INTO `creature_ai_texts` VALUES ('-1300', 'Bully!', null, null, null, null, null, null, null, null, '11167', '1', '0', '0', '19219');
INSERT INTO `creature_ai_texts` VALUES ('-1301', 'Go ahead, gimme your best shot. I can take it!', null, null, null, null, null, null, null, null, '11166', '1', '0', '0', '19219');
INSERT INTO `creature_ai_texts` VALUES ('-1302', 'Think you can hurt me, huh? Think I\'m afraid of you?!', null, null, null, null, null, null, null, null, '11165', '1', '0', '0', '19219');
INSERT INTO `creature_ai_texts` VALUES ('-1303', 'The work must continue.', null, null, null, null, null, null, null, null, '10376', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-1304', 'Don\'t make me kill you!', null, null, null, null, null, null, null, null, '10378', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-1305', 'You brought this on yourselves.', null, null, null, null, null, null, null, null, '10379', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-1306', 'It had to be done.', null, null, null, null, null, null, null, null, '10380', '1', '0', '0', '17941');
