INSERT INTO `creature_ai_texts` VALUES ('-1308', 'I... Deserve this.', null, null, null, null, null, null, null, null, '10382', '1', '0', '0', '17941');
INSERT INTO `creature_ai_texts` VALUES ('-1309', 'grows in size upon seeing $N!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '17725');
INSERT INTO `creature_ai_texts` VALUES ('-1310', 'strength fades!', null, null, null, null, null, null, null, null, '0', '2', '0', '0', '17725');
INSERT INTO `creature_ai_texts` VALUES ('-1311', 'Illidan reigns!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', '17727');
INSERT INTO `creature_ai_texts` VALUES ('-1312', 'The Legion\'s final conquest has begun! Once again the subjugation of this world is within our grasp. Let none survive!', null, null, null, null, null, null, null, null, '11022', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1313', 'Crumble and rot!', null, null, null, null, null, null, null, null, '11023', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1314', 'Ashes to ashes, dust to dust', null, null, null, null, null, null, null, null, '11055', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1315', 'Succumb to the icy chill... of death!', null, null, null, null, null, null, null, null, '11024', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1316', 'It will be much colder in your grave', null, null, null, null, null, null, null, null, '11058', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1317', 'You have won this battle, but not... the... war', null, null, null, null, null, null, null, null, '11026', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1318', 'All life must perish!', null, null, null, null, null, null, null, null, '11025', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1319', 'Victory to the Legion!', null, null, null, null, null, null, null, null, '11057', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1320', 'Your world is ours now.', null, null, null, null, null, null, null, null, '11056', '1', '0', '0', '17767');
INSERT INTO `creature_ai_texts` VALUES ('-1321', 'Abandon all hope! The Legion has returned to finish what was begun so many years ago. This time, there will be no escape!', null, null, null, null, null, null, null, null, '10999', '1', '0', '0', '17842');
INSERT INTO `creature_ai_texts` VALUES ('-1322', 'Reesh, hokta!', null, null, null, null, null, null, null, null, '11001', '1', '0', '0', '17842');
INSERT INTO `creature_ai_texts` VALUES ('-1323', 'Don\'t fight it', null, null, null, null, null, null, null, null, '11047', '1', '0', '0', '17842');
INSERT INTO `creature_ai_texts` VALUES ('-1324', 'No one is going to save you', null, null, null, null, null, null, null, null, '11048', '1', '0', '0', '17842');
INSERT INTO `creature_ai_texts` VALUES ('-1325', 'Your time is almost... up', null, null, null, null, null, null, null, null, '11002', '1', '0', '0', '17842');
INSERT INTO `creature_ai_texts` VALUES ('-1326', 'Just a taste... of what awaits you', null, null, null, null, null, null, null, null, '11046', '1', '0', '0', '17842');
