INSERT INTO `db_script_string` VALUES ('2000000729', 'You don\'t remember me, do you? Blasted Scourge... They\'ve tried to drain you of everything that made you a righteous force of reckoning. \r\n\r\n Every last ounce of good... Everything that made you a tauren!', null, null, null, null, null, null, null, null);
