INSERT INTO `locales_page_text` VALUES ('751', null, null, 'Hier sind die MaÃŸe fÃ¼r den Kolben:$B$BLÃ¤nge: 32 BHB$BBreite: 12 BHB$BGewicht: 23,5 KKG bis 25,5 KKG$BZÃ¼ndung: 70 ZL$BSchubkraft: Mindestens 98$BVerdichtungsrate: Super-Derb oder hÃ¶her$B$BFalls Euch GnomenmaÃŸe nicht mehr gelÃ¤ufig sind:$BBHB = Barthaaresbreite$BKKG = Klitzekleine Gramm$BZL = Zehenlocken', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('752', null, null, 'Ja, Mann. Ihr bringen diesen Brief zu Frang in Tal der PrÃ¼fungen. Er meist vor dem Bau und peitschen junge Krieger zurecht, sodass kÃ¤mpfen kÃ¶nnen fÃ¼r Horde.$B$BEr brauchen schlaue Krieger wie Euch, um Orcs zu zeigen, dass KÃ¤mpfen auch anders geht, nicht nur mit Muskelns. Ihr schneller und schlauer als die meisten seid, aber damit lieber nicht prahlen - Orcs nehmen Beleidigung nicht auf leichtige Schulter. Sie gute VerbÃ¼ndete werden sein und vielleicht Euch auch lehren das eine oder das andere.$B$B- Tar\'sere ', null, null, null, null, null);
