INSERT INTO `locales_page_text` VALUES ('832', null, null, 'Versengte SpinnenfangzÃ¤hne:$B$BDas Pyrophorerz aus den HÃ¶hlen in der NÃ¤he des Thelgenfelsens hat die dort lebenden Spinnen versengt.$B$BDas Gift in ihren FangzÃ¤hnen kocht und reagiert mit den FangzÃ¤hnen und produziert dadurch eine sehr harte Substanz.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('833', null, null, 'Verkohlte HÃ¶rner:$B$BEbenso wie die versengten SpinnenfangzÃ¤hne haben auch die verkohlten HÃ¶rner der im Verbrannten Tal lebenden SchimÃ¤ren aus dem Steinkrallengebirge auf Kalimdor wertvolle Eigenschaften. $B$BFlachgeklopft und in geeigneter Weise behandelt werden sie gegen mechanische Belastungen Ã¤uÃŸerst robust.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('834', null, null, 'Galvanisiertes Horn:$B$BDas galvanisierte Horn gibt der gesamten RÃ¼stung, die ich fÃ¼r Euch machen werde, erst ihre HÃ¤rte. Daher brauchen wir das Horn unbedingt.$B$BIhr bekommt es von der seltenen SchimÃ¤renmatriarchin im verbrannten Tal.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('835', null, null, 'Phiole mit Phlogiston:$B$BIn der letzten Phase brauche ich zur Herstellung der RÃ¼stung eine Ã¤uÃŸerst heiÃŸe Schmiede. Damit die Schmiede heiÃŸ genug wird, brauche ich wiederum eine Phiole mit Phlogiston.$B$BDer einzige bekannte Hersteller dieser Substanz ist der berÃ¼hmte Stacheleber Roogug, und der wird sie nicht kampflos hergeben. Roogug lebt zurÃ¼ckgezogen im Kral der Klingenhauer im sÃ¼dlichen Brachland.', null, null, null, null, null);
