INSERT INTO `locales_page_text` VALUES ('971', null, null, 'Uldaman, Tag 4$BErste Hinweise ergaben sich tief in Uldaman selbst, als einer der wildesten Troggs, die ich je gesehen habe, einen groÃŸen Saphir herumzeigte - der wohl perfekt zur Halskette passen wÃ¼rde. Ich hÃ¶rte, wie einer der Zwerge der Schattenschmiede den Namen der Bestie nannte: Grimlok. Allerdings sind die vielen WÃ¤chter, die diese Bestie Grimlok hat,...', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('972', null, null, 'Uldaman, 5. Tag$B...der Trogg nahm seinen getÃ¶teten \"BrÃ¼dern\" den Topas ab und ging in die NebenhÃ¶hle nahe dem Eingang der Halle...$B...legte den Topas hastig in eine groÃŸe, verdÃ¤chtige Urne im hinteren Teil der Kammer...$B...ausgerechnet Zwerge! Drei an der Zahl. Momentan haben sie sich vor den Troggs verbarrikadiert. Sie zu rufen wÃ¤re einem Selbstmord gleichgekommen, denn in meinem geschwÃ¤chten Zustand hÃ¤tten mich die Troggs niedergemacht. Aber wenn ich den dritten Edelstein kenne, werde ich...', null, null, null, null, null);
