INSERT INTO `locales_page_text` VALUES ('1436', null, null, 'Pelver Deepstomp$B$BDunkelbewahrer Pelver ist unser am hÃ¶chsten verehrter JÃ¼nger. Er hat den SchlÃ¼ssel lÃ¤nger getragen als jeder andere, und das unter enormen Opfern. Wenn er zu seiner BÃ¼rde gerufen wird, so wird er im Domizil bewacht.$B$BSein Opfer wird gewÃ¼rdigt und er wird viele Jahre in unseren Herzen weiterleben... nachdem die Dunkelheit ihn geholt hat.', null, null, null, null, null);
