INSERT INTO `locales_page_text` VALUES ('1471', null, null, 'Solomon,$B$Bder Ãœberbringer dieses Dekrets hat den offiziellen Status eines Stellvertreters von Sturmwind erlangt. Ihr kÃ¶nnt ihn benutzen, um einen Beweis dafÃ¼r zu finden, dass der schwarze Drachenschwarm sich mit den Orcs des Schwarzfelsklans eingelassen hat. Sollte so ein Beweis gefunden werden, soll dieser Stellvertreter besagten Beweis zu mir nach Sturmwind bringen, woraufhin ich den Befehl erteilen werde, hinreichend MilitÃ¤r zur UnterstÃ¼tzung von Seenhain zu entsenden.$B$BHochachtungsvoll$B$B$B$BHochlord Bolvar Fordragon', null, null, null, null, null);
