INSERT INTO `locales_page_text` VALUES ('1511', null, null, '<HTML>$B<BODY>$B<H1 align=\"center\">$BKapitel 1: DER NÃ–RDLICHE PYLON$B</H1>$B<BR/>$B<BR/>$B<IMG src=\"Interface\\Pictures\\11482_crystals_mini_north\" align=\"left\"/>$B<P align=\"right\">Der nÃ¶rdliche<BR/>$BPylon akzeptiert<BR/>$Bgelbe, rote<BR/>$Bund grÃ¼ne<BR/>$BKristalle.</P>$B<BR/>$B<BR/>$B<BR/>$B<BR/>$B<P align=\"left\">Der Pylon fokussiert auf die Energie, die mit den gelben Kristallen als Hauptenergiequelle erzeugt werden kann.</P>$B</BODY>$B</HTML>', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1512', null, null, '<HTML>$B<BODY>$B<H1 align=\"center\">$BNÃ¶rdliche Kristallkombinationen$B</H1>$B<BR/>$B<P align=\"left\">Es gibt zwei mÃ¶gliche Effekte: Einer, der Schaden erzeugt, und einen, der Schaden verhindert. </P>$B<BR/>$B<P align=\"left\">KRISTALLSPRENGLADUNG: Erfordert rote und gelbe Kristalle. <BR/>Damit wird eine gewaltige Explosion erzeugt, die Eure Feinde lÃ¤hmt.</P>$B<BR/>$B<P align=\"left\">KRISTALLFLICKER: Erfordert gelbe und grÃ¼ne Kristalle. <BR/>Das verleiht Euch eine heilende Aura.$B</P>$B</BODY>$B</HTML>', null, null, null, null, null);
