INSERT INTO `locales_page_text` VALUES ('1515', null, null, '<HTML>$B<BODY>$B<H1 align=\"center\">$BÃ–stliche Kristallkombinationen$B</H1>$B<BR/>$B<P align=\"left\">Es gibt zwei mÃ¶gliche Effekte: Einer verleiht der Willenskraft einen Bonus, einer erzeugt einen Schaden zufÃ¼genden Schild.</P>$B<BR/>$B<P align=\"left\">KRISTALLKRAFT: Erfordert blaue und grÃ¼ne Kristalle.<BR/>Das verleiht Euch eine ErhÃ¶hung der Willenskraft.</P>$B<BR/>$B<P align=\"left\">KRISTALLSPITZE: Erfordert blaue und gelbe Kristalle.<BR/>Damit wird ein Schild erzeugt, der Angreifern Schaden zufÃ¼gt.$B</P>$B</BODY>$B</HTML>', null, null, null, null, null);
