INSERT INTO `locales_page_text` VALUES ('1532', null, null, '<HTML>$B<BODY>$B<H1 align=\"center\">$BWestliche Kristallkombinationen$B</H1>$B<BR/>$B<P align=\"left\">Es gibt zwei mÃ¶gliche Effekte: Einer erhÃ¶ht Euren Schutz vor Schaden, einer schwÃ¤cht die Verteidigung des Gegners.</P>$B<BR/>$B<P align=\"left\">KRISTALLBARRIERE: Erfordert rote und grÃ¼ne Kristalle.<BR/>Das verleiht Euch eine ErhÃ¶hung der RÃ¼stung.</P>$B<BR/>$B<P align=\"left\">KRISTALLSCHWÃ„CHER: Erfordert blaue und rote Kristalle.<BR/>Damit kann die RÃ¼stung eines bestimmten Ziels verringert werden.$B</P>$B</BODY>$B</HTML>', null, null, null, null, null);
