INSERT INTO `locales_page_text` VALUES ('1551', null, null, 'Seid gegrÃ¼ÃŸt und willkommen, Abenteurer!$B$BIch, Krinkle Goodsteel, brauche Leute, die bereit sind, ihr Leben fÃ¼r ein Ã¤uÃŸerst schwieriges Unterfangen zu riskieren. Aufgrund der zahlreichen Pflichten und des damit verbundenen Arbeitspensums versichere ich, dass jeder, der tapfer genug ist, diese Liste von GegenstÃ¤nden fertig zu stellen und sie mir unversehrt zu bringen, reich belohnt werden soll. $B$BDie Liste befindet sich auf den folgenden Seiten...', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1552', null, null, 'Robuste KristallbeinschÃ¤fte - $B$BDie Glasnetzspinnen der sengenden Schlucht bestehen aus einem unglaublichen, kristallÃ¤hnlichen Material. Wenn ihre Beine beim Erlegen nicht beschÃ¤digt werden, taugen sie als erstklassige Leiter bei einigen Ingenieurskunstexperimenten meiner GefÃ¤hrten.$B$BIch benÃ¶tige ein paar davon fÃ¼r Tests. Die Spinnen mit ihren acht Beinen dÃ¼rften fÃ¼r einen wackeren Krieger oder geÃ¼bten Magier kein Problem darstellen.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1553', null, null, 'Ein Ã¼berfÃ¤lliges PÃ¤ckchen.$B$BIch hatte einen Piloten angeheuert, den ich fÃ¼r zuverlÃ¤ssig hielt, damit er mir ein PÃ¤ckchen aus Theramore bringt. Offenbar habe ich mich geirrt. Der Pilot hat zwar Ã¼berlebt, lieÃŸ aber seinen gesamten Zeppelin und dessen Fracht zurÃ¼ck, um den eigenen Hals zu retten.$B$BAlso, ich brauche dieses PÃ¤ckchen unbedingt. Wie Beezil mir sagte, musste er westlich der Bucht in den Marschen von Dustwallow notlanden. Er stÃ¼rzte unmittelbar hinter den HÃ¼geln an der KÃ¼ste ab.$B$BHmmm... er hat mir nie gesagt, warum er notlanden musste...', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('1554', null, null, 'Goodsteels ausbalancierter Flamberg. $B$BZuletzt mÃ¶chte ich noch mein bis zum heutigen Tag bestes SchmiedestÃ¼ck. Dieser Flamberg war ein unglaubliches Schwert, das ich einem, wie ich glaubte, hÃ¶chst ehrbaren und groÃŸen Krieger Ã¼berlieÃŸ. Tja, er wurde von einem Murloc namens Jarquai in die Mangel genommen. So was in der Art jedenfalls. $B$BEs war so: Als er an der KÃ¼ste der SÃ¼mpfe des Elends entlangreiste, kam es ihm in den Sinn, die Kreatur anzugreifen. Er unterlag und floh offenbar, lieÃŸ aber seine Waffe zurÃ¼ck, die der Murloc an sich nahm.', null, null, null, null, null);
