INSERT INTO `locales_page_text` VALUES ('1711', null, null, 'Dringende Nachricht fÃ¼r KlippenbehÃ¼ter Longhorn$B$BEine groÃŸe Zentaurenstreitmacht aus dem Camp E\'thok hat eine unserer Nachschubkarawanen Ã¼berfallen. Alle Mitglieder der Karawane sind verloren, ebenso die VorrÃ¤te. Dies war ein beispiellos dreister Angriff der Zentauren, aber, wie ich fÃ¼rchte, nicht der letzte.', null, null, null, null, null);
