INSERT INTO `locales_page_text` VALUES ('1731', null, null, 'Hier ruht KÃ¶nig Terenas Menethil II. - der letzte wahre KÃ¶nig von Lordaeron.$B$BGroÃŸ waren seine Taten - lang wÃ¤hrte seine Herrschaft - undenkbar schien sein Tod. $B$B         \"MÃ¶ge der Vater unschuldig an des Sohnes Taten ruhen. $B          MÃ¶ge die blutige Krone verloren und vergessen bleiben.\"', null, null, null, null, null);
