INSERT INTO `locales_page_text` VALUES ('2271', null, null, 'Nataka, in Tausend Nadeln steht alles zum Besten. Die Zentauren sind verÃ¤rgert, aber das sind wir gewÃ¶hnt... gelegentliche Angriffe, aber sonst nichts, was man als eine ernsthafte Bedrohung betrachten kÃ¶nnte. Es scheinen jedoch Neuigkeiten im Umlauf zu sein, wonach sich die Zentaurenklans zusammenschlieÃŸen sollen.$B$BWir haben uns schon immer Gedanken gemacht, welche Gefahr der Zusammenschluss eines solch unbarmherzigen Volkes wie den Zentauren darstellen wÃ¼rde... Ich mag gar nicht nÃ¤her darÃ¼ber nachdenken, denn wie Ihr wisst... kÃ¶nnte nichts den tiefen Hass Ã¼berwinden, den sie in sich tragen... sogar aufeinander.$B$B-KlippenbehÃ¼ter Longhorn', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2290', null, null, 'Nataka, in Tausend Nadeln steht alles zum Besten. Die Zentauren sind verÃ¤rgert, aber das sind wir gewÃ¶hnt... gelegentliche Angriffe, aber sonst nichts, was man als eine ernsthafte Bedrohung betrachten kÃ¶nnte. Es scheinen jedoch Neuigkeiten im Umlauf zu sein, wonach sich die Zentaurenklans zusammenschlieÃŸen sollen.$B$BWir haben uns schon immer Gedanken gemacht, welche Gefahr der Zusammenschluss eines solch unbarmherzigen Volkes wie den Zentauren darstellen wÃ¼rde... Ich mag gar nicht nÃ¤her darÃ¼ber nachdenken, denn wie Ihr wisst... kÃ¶nnte nichts den tiefen Hass Ã¼berwinden, den sie in sich tragen... sogar aufeinander.$B$B-KlippenbehÃ¼ter Longhorn', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('2291', null, null, 'Erzbischof Alonsus Faol$BGÃ¼tiger Schutzherr der Kirche des Lichts.$BObwohl Erzbischof Faol mit seinem guten Werk in Stratholme, Lordaeron begann, so war er doch den Leuten von Sturmwind ein guter Freund. In den dunklen Tagen nach dem Zweiten Krieg trug er dazu bei, die Bruderschaft von Nordhain zu reformieren und sammelte Gelder, um die Stadt Sturmwind wieder aufzubauen.$BDiese Kathedrale steht jetzt nicht nur hier als Bastion des Heiligen Lichts, sondern als das dauerhafte Testament an den edlen Geist und die GroÃŸzÃ¼gigkeit eines ganz besonderen Mannes.', null, null, null, null, null);
