INSERT INTO `locales_page_text` VALUES ('2414', null, null, 'Feuerwasser.$B$BMuss mehr machen. Mehr Feuerwasser.$B$BFeuerwasser machen Winterfelle stark. Unsere Feinde besiegen.$B$BWinterfelle haben keine VerbÃ¼ndeten. Winterfelle sind stark. Haben groÃŸe Macht.', null, null, null, null, null);
