INSERT INTO `locales_page_text` VALUES ('2631', null, null, '<Die Seiten sind mit alten Elfenrunen bedeckt.>$BAuf diesen Seiten finden sich Erinnerungen an Ereignisse, die sich bei der Sammlung und Herstellung der zur Anfertigung von groÃŸem Arkanum erforderlichen Reagenzien zutrugen.$BMÃ¶ge dieser Buchband niemals in die HÃ¤nde unserer Feinde geraten. $BMÃ¶ge ich dereinst wieder das fahle Licht des Mondes auf Quel\'Thalas scheinen sehen.$BMÃ¶ge ich zum Ruhme von Kael\'thas sterben.$BMÃ¶ge ich zum Ruhme Illidans tÃ¶ten.$B- Meister Kariel Winthalus', null, null, null, null, null);
