INSERT INTO `locales_page_text` VALUES ('2654', null, null, '<HTML>$B<BODY>$B<H1 align=\"center\">ABHANDLUNG MILITÃ„RISCHER RÃ„NGE</H1>$B<BR/>$B<BR/>$B<IMG src=\"Interface\\PvPRankBadges\\PvPRankAlliance\" align=\"left\"/>$B<BR/>$B<P align=\"right\">Es folgen</P>$B<P align=\"right\">die militÃ¤rischen RÃ¤nge</P>$B<P align=\"right\">der Allianz,</P>$B<P align=\"right\">gefolgt von</P>$B<P align=\"right\">informationen die</P>$B<P align=\"right\">den VÃ¶lkern</P>$B<P align=\"right\">der Horde bekannt sind. Alle</P>$B<P align=\"right\">Angaben sind in zwei</P>$B<P align=\"right\">Abschnitte unterteilt, Offiziere und Soldaten. Dabei werden ihre RÃ¤nge jeweils in absteigender Reihenfolge aufgelistet, vom hÃ¶chsten bis zum niedrigsten Rang. Lang lebe die Allianz!</P>$B</BODY>$B</HTML>', null, null, null, null, null);
