INSERT INTO `locales_page_text` VALUES ('2661', null, null, '<HTML>$B<BODY>$B<H1 align=\"center\">MILITÃ„RISCHE RÃ„NGE DER HORDE &amp; ALLIANZ</H1>$B<BR/>$B<BR/>$B<IMG src=\"Interface\\PvPRankBadges\\PvPRankHorde\" align=\"left\"/>$B<BR/>$B<P align=\"right\">Throm\'ka!  In diesem</P>$B<P align=\"right\">buch findet Ihr</P>$B<P align=\"right\">zahlreiche Auflistungen</P>$B<P align=\"right\">der militÃ¤rischen RÃ¤nge</P>$B<P align=\"right\">der Horde und</P>$B<P align=\"right\">der erbÃ¤rmlichen Allianz.</P>$B<P align=\"right\">Wir beginnen mit der</P>$B<P align=\"right\">Horde, dann folgt die Allianz,</P>$B<P align=\"right\">jeweils mit einer Liste der Offiziers- und SoldatenrÃ¤nge. GebÃ¼hrenderweise werden die StÃ¤rksten zu Beginn und die SchwÃ¤cheren darunter aufgelistet.</P>$B</BODY>$B</HTML>', null, null, null, null, null);
