INSERT INTO `locales_page_text` VALUES ('2671', null, null, 'Simone die VerfÃ¼hrerin:$B $B Findet Simone beim Besudeln des Krater von Un\'Goros. Lasst Euch von Ihrer Verkleidung nicht tÃ¤uschen. NÃ¤hert Euch mit Bedacht und fordert sie zum Kampf heraus. ', null, null, null, null, null);
