INSERT INTO `locales_page_text` VALUES ('3535', null, null, 'Hochlord,$B$N hat den Feldzug gegen die GeiÃŸel von GeiÃŸelholme erfolgreich angefÃ¼hrt und uns in Stellung gebracht, um schlieÃŸlich auf Eiskrone zu marschieren.$BDie Maurer wurden mit Material versorgt, die sie benÃ¶tigen, um mit dem Bau der Festung der Gerechtigkeit zu beginnen - erneut gebÃ¼hrt unser Dank $N fÃ¼r $gseine:ihre; UnterstÃ¼tzung im Kristallsangwald.$BEine letzte Herausforderung gilt es noch zu meistern: die Einnahme des Kreuzfahrerturms. Denn an diesem Ort, an dem wir am meisten gelitten haben, mÃ¶chte ich dieses letzte Kapitel seinen Anfang nehmen lassen.$BWir warten auf Euren Befehl, Herr.$BMÃ¶ge Euch das Licht Schutz gebieten.$B-Gustav', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3536', null, null, 'MajestÃ¤t, $B$BOh, gnÃ¤diger KÃ¶nig, mÃ¶ge das Licht Eure Hand fÃ¼hren und Euer Herz erhellen. Wartet noch mit Eurem Urteil Ã¼ber diesen gefallenen Helden - und sei es nur fÃ¼r einen Moment - aber hÃ¶rt mich an.$B$B$GDer TrÃ¤ger:Die TrÃ¤gerin; dieses Briefs ist ein ehemaliger Streiter der Allianz. Obwohl der Gestank des Todes und das Erscheinungsbild der GeiÃŸel an $Gihm:ihr; klebt, so hat $Ger:sie; doch die Seele eines Helden. Eine Seele, die erst vor Kurzem in ihren KÃ¶rper zurÃ¼ckkehrte.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3537', null, null, '$N reprÃ¤sentiert eine neue, vereinigte Front gegen den LichkÃ¶nig, die sich die Ritter der Schwarzen Klinge nennen. Ja, MajestÃ¤t, Todesritter haben sich gegen ihren frÃ¼heren Herrn gewandt. Sie werden vom Sohn des FÃ¼rsten Alexandros Mograine angefÃ¼hrt - dem Aschenbringer. Sie verfolgen dasselbe Ziel wie wir, das Ziel, das alle verfolgen: das Ende der GeiÃŸel.$B$BIch verlange nicht, dass Ihr $N und die Ritter der Schwarzen Klinge in die Allianz aufnehmt, ich bitte nur um Toleranz.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3538', null, null, 'Denkt daran, MajestÃ¤t, durch Ehre und Blut dienen wir alle.$B$BHochachtungsvoll,$B$BHochlord Tirion Fordring', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3539', null, null, 'KriegshÃ¤uptling, $B$BmÃ¤chtiger KriegshÃ¤uptling, mÃ¶ge das Licht Eure Hand fÃ¼hren und Euer Herz erhellen. Wartet noch mit Eurem Urteil Ã¼ber diesen gefallenen Helden - und sei es nur fÃ¼r einen Moment - aber hÃ¶rt mich an.$B$B$gDer TrÃ¤ger:Die TrÃ¤gerin; dieses Briefs ist ein ehemaliger Streiter der Horde. Obwohl der Gestank des Todes und das Erscheinungsbild der GeiÃŸel an $gihm:ihr; klebt, so hat $ger:sie; doch die Seele eines Helden. Eine Seele, die erst vor Kurzem in ihren KÃ¶rper zurÃ¼ckkehrte.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3540', null, null, '$N reprÃ¤sentiert eine neue, vereinigte Front gegen den LichkÃ¶nig, die sich die Ritter der Schwarzen Klinge nennen. Ja, KriegshÃ¤uptling, Todesritter haben sich gegen ihren frÃ¼heren Herrn gewandt. Sie werden vom Sohn des FÃ¼rsten Alexandros Mograine angefÃ¼hrt - dem Aschenbringer. Sie verfolgen dasselbe Ziel wie wir, das Ziel, das alle verfolgen: das Ende der GeiÃŸel.$B$BIch verlange nicht, dass Ihr $N und die Ritter der Schwarzen Klinge in die Horde aufnehmt, ich bitte nur um Toleranz.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3541', null, null, 'Denkt daran, Thrall, durch Blut und Donner dienen wir alle.$B$BHochachtungsvoll,$B$BHochlord Tirion Fordring$B$BP.S. Richtet bitte Etrigg meine GrÃ¼ÃŸe aus und sagt ihm, dass ich in Nordend einen guten Orc an meiner Seite zu schÃ¤tzen wÃ¼sste - mit Eurer Erlaubnis, natÃ¼rlich.', null, null, null, null, null);
INSERT INTO `locales_page_text` VALUES ('3542', null, null, 'Erzmagier Antonidas, GroÃŸmagus der Kirin Tor$BDie groÃŸartige Stadt Dalaran steht wieder - ein Zeugnis der Beharrlichkeit und des Willen ihres grÃ¶ÃŸten Sohnes.$BEuer Opfer wird nicht vergebens gewesen sein, werter Freund.$BIn Liebe und Ehre,$BJaina Prachtmeer', null, null, null, null, null);
