INSERT INTO `script_texts` VALUES ('-1542010', 'Perhaps I can find a use for you.', null, null, null, null, null, null, null, null, '10287', '1', '0', '0', 'the_maker SAY_AGGRO_2');
INSERT INTO `script_texts` VALUES ('-1542011', 'Anger... Hate... These are tools I can use.', null, null, null, null, null, null, null, null, '10288', '1', '0', '0', 'the_maker SAY_AGGRO_3');
