INSERT INTO `script_texts` VALUES ('-1544000', 'Wretched, meddling insects. Release me and perhaps i will grant you a merciful death!', null, null, null, null, null, null, null, null, '10247', '1', '0', '0', 'magtheridon SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1544001', 'Vermin! Leeches! Take my blood and choke on it!', null, null, null, null, null, null, null, null, '10248', '1', '0', '0', 'magtheridon SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1544002', 'Illidan is an arrogant fool. I will crush him and reclaim Outland as my own.', null, null, null, null, null, null, null, null, '10249', '1', '0', '0', 'magtheridon SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1544003', 'Away, you mindless parasites. My blood is my own!', null, null, null, null, null, null, null, null, '10250', '1', '0', '0', 'magtheridon SAY_TAUNT4');
INSERT INTO `script_texts` VALUES ('-1544004', 'How long do you believe your pathetic sorcery can hold me?', null, null, null, null, null, null, null, null, '10251', '1', '0', '0', 'magtheridon SAY_TAUNT5');
INSERT INTO `script_texts` VALUES ('-1544005', 'My blood will be the end of you!', null, null, null, null, null, null, null, null, '10252', '1', '0', '0', 'magtheridon SAY_TAUNT6');
