INSERT INTO `script_texts` VALUES ('-1544010', 'Did you think me weak? Soft? Who is the weak one now?!', null, null, null, null, null, null, null, null, '10255', '1', '0', '0', 'magtheridon SAY_PLAYER_KILLED');
INSERT INTO `script_texts` VALUES ('-1544011', 'The Legion...will consume you...all...', null, null, null, null, null, null, null, null, '10258', '1', '0', '0', 'magtheridon SAY_DEATH');
