INSERT INTO `script_texts` VALUES ('-1560004', 'Did you really think you would leave here alive?', null, null, null, null, null, null, null, null, '10410', '1', '0', '0', 'skarloc SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1560005', 'Guards! Urgh..Guards..!', null, null, null, null, null, null, null, null, '10411', '1', '0', '0', 'skarloc SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1560006', 'You there, fetch water quickly! Get these flames out before they spread to the rest of the keep! Hurry, damn you!', null, null, null, null, null, null, null, null, '10428', '1', '0', '0', 'lieutenant_drake SAY_ENTER');
INSERT INTO `script_texts` VALUES ('-1560007', 'I know what you\'re up to, and I mean to put an end to it, permanently!', null, null, null, null, null, null, null, null, '10429', '1', '0', '0', 'lieutenant_drake SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1560008', 'No more middling for you.', null, null, null, null, null, null, null, null, '10432', '1', '0', '0', 'lieutenant_drake SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1560009', 'You will not interfere!', null, null, null, null, null, null, null, null, '10433', '1', '0', '0', 'lieutenant_drake SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1560010', 'Time to bleed!', null, null, null, null, null, null, null, null, '10430', '1', '0', '0', 'lieutenant_drake SAY_MORTAL');
INSERT INTO `script_texts` VALUES ('-1560011', 'Run, you blasted cowards!', null, null, null, null, null, null, null, null, '10431', '1', '0', '0', 'lieutenant_drake SAY_SHOUT');
INSERT INTO `script_texts` VALUES ('-1560012', 'Thrall... must not... go free.', null, null, null, null, null, null, null, null, '10434', '1', '0', '0', 'lieutenant_drake SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1560013', 'Thrall! Come outside and face your fate!', null, null, null, null, null, null, null, null, '10418', '1', '0', '0', 'epoch SAY_ENTER1');
INSERT INTO `script_texts` VALUES ('-1560014', 'Taretha\'s life hangs in the balance. Surely you care for her. Surely you wish to save her...', null, null, null, null, null, null, null, null, '10419', '1', '0', '0', 'epoch SAY_ENTER2');
INSERT INTO `script_texts` VALUES ('-1560015', 'Ah, there you are. I had hoped to accomplish this with a bit of subtlety, but I suppose direct confrontation was inevitable. Your future, Thrall, must not come to pass and so...you and your troublesome friends must die!', null, null, null, null, null, null, null, null, '10420', '1', '0', '0', 'epoch SAY_ENTER3');
INSERT INTO `script_texts` VALUES ('-1560016', 'Enough! I will erase your very existence!', null, null, null, null, null, null, null, null, '10421', '1', '0', '0', 'epoch SAY_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1560017', 'You cannot fight fate!', null, null, null, null, null, null, null, null, '10422', '1', '0', '0', 'epoch SAY_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1560018', 'You are...irrelevant.', null, null, null, null, null, null, null, null, '10425', '1', '0', '0', 'epoch SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1560019', 'Thrall will remain a slave. Taretha will die. You have failed.', null, null, null, null, null, null, null, null, '10426', '1', '0', '0', 'epoch SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1560020', 'Not so fast!', null, null, null, null, null, null, null, null, '10423', '1', '0', '0', 'epoch SAY_BREATH1');
INSERT INTO `script_texts` VALUES ('-1560021', 'Struggle as much as you like!', null, null, null, null, null, null, null, null, '10424', '1', '0', '0', 'epoch SAY_BREATH2');
INSERT INTO `script_texts` VALUES ('-1560022', 'No!...The master... will not... be pleased.', null, null, null, null, null, null, null, null, '10427', '1', '0', '0', 'epoch SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1560023', 'Very well then. Let\'s go!', null, null, null, null, null, null, null, null, '10465', '1', '0', '0', 'thrall hillsbrad SAY_TH_START_EVENT_PART1');
INSERT INTO `script_texts` VALUES ('-1560024', 'As long as we\'re going with a new plan, I may aswell pick up a weapon and some armor.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'thrall hillsbrad SAY_TH_ARMORY');
INSERT INTO `script_texts` VALUES ('-1560025', 'A rider approaches!', null, null, null, null, null, null, null, null, '10466', '0', '0', '0', 'thrall hillsbrad SAY_TH_SKARLOC_MEET');
INSERT INTO `script_texts` VALUES ('-1560026', 'I\'ll never be chained again!', null, null, null, null, null, null, null, null, '10467', '1', '0', '0', 'thrall hillsbrad SAY_TH_SKARLOC_TAUNT');
INSERT INTO `script_texts` VALUES ('-1560027', 'Very well. Tarren Mill lies just west of here. Since time is of the essence...', null, null, null, null, null, null, null, null, '10468', '1', '0', '0', 'thrall hillsbrad SAY_TH_START_EVENT_PART2');
INSERT INTO `script_texts` VALUES ('-1560028', 'Let\'s ride!', null, null, null, null, null, null, null, null, '10469', '0', '0', '1', 'thrall hillsbrad SAY_TH_MOUNTS_UP');
INSERT INTO `script_texts` VALUES ('-1560029', 'Taretha must be in the inn. Let\'s go.', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'thrall hillsbrad SAY_TH_CHURCH_END');
INSERT INTO `script_texts` VALUES ('-1560030', 'Taretha! What foul magic is this?', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'thrall hillsbrad SAY_TH_MEET_TARETHA');
INSERT INTO `script_texts` VALUES ('-1560031', 'Who or what was that?', null, null, null, null, null, null, null, null, '10470', '1', '0', '1', 'thrall hillsbrad SAY_TH_EPOCH_WONDER');
INSERT INTO `script_texts` VALUES ('-1560032', 'No!', null, null, null, null, null, null, null, null, '10471', '1', '0', '5', 'thrall hillsbrad SAY_TH_EPOCH_KILL_TARETHA');
INSERT INTO `script_texts` VALUES ('-1560033', 'Goodbye, Taretha. I will never forget your kindness.', null, null, null, null, null, null, null, null, '10472', '1', '0', '0', 'thrall hillsbrad SAY_TH_EVENT_COMPLETE');
INSERT INTO `script_texts` VALUES ('-1560034', 'Things are looking grim...', null, null, null, null, null, null, null, null, '10458', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP1');
INSERT INTO `script_texts` VALUES ('-1560035', 'I will fight to the last!', null, null, null, null, null, null, null, null, '10459', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_LOW_HP2');
INSERT INTO `script_texts` VALUES ('-1560036', 'Taretha...', null, null, null, null, null, null, null, null, '10460', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_DIE1');
INSERT INTO `script_texts` VALUES ('-1560037', 'A good day...to die...', null, null, null, null, null, null, null, null, '10461', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_DIE2');
INSERT INTO `script_texts` VALUES ('-1560038', 'I have earned my freedom!', null, null, null, null, null, null, null, null, '10448', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO1');
INSERT INTO `script_texts` VALUES ('-1560039', 'This day is long overdue. Out of my way!', null, null, null, null, null, null, null, null, '10449', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO2');
INSERT INTO `script_texts` VALUES ('-1560040', 'I am a slave no longer!', null, null, null, null, null, null, null, null, '10450', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO3');
INSERT INTO `script_texts` VALUES ('-1560041', 'Blackmoore has much to answer for!', null, null, null, null, null, null, null, null, '10451', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_AGGRO4');
INSERT INTO `script_texts` VALUES ('-1560042', 'You have forced my hand!', null, null, null, null, null, null, null, null, '10452', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_KILL1');
INSERT INTO `script_texts` VALUES ('-1560043', 'It should not have come to this!', null, null, null, null, null, null, null, null, '10453', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_KILL2');
INSERT INTO `script_texts` VALUES ('-1560044', 'I did not ask for this!', null, null, null, null, null, null, null, null, '10454', '1', '0', '0', 'thrall hillsbrad SAY_TH_RANDOM_KILL3');
INSERT INTO `script_texts` VALUES ('-1560045', 'I am truly in your debt, strangers.', null, null, null, null, null, null, null, null, '10455', '1', '0', '0', 'thrall hillsbrad SAY_TH_LEAVE_COMBAT1');
INSERT INTO `script_texts` VALUES ('-1560046', 'Thank you, strangers. You have given me hope.', null, null, null, null, null, null, null, null, '10456', '1', '0', '0', 'thrall hillsbrad SAY_TH_LEAVE_COMBAT2');
INSERT INTO `script_texts` VALUES ('-1560047', 'I will not waste this chance. I will seek out my destiny.', null, null, null, null, null, null, null, null, '10457', '1', '0', '0', 'thrall hillsbrad SAY_TH_LEAVE_COMBAT3');
INSERT INTO `script_texts` VALUES ('-1560048', 'I\'m free! Thank you all!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'taretha SAY_TA_FREE');
INSERT INTO `script_texts` VALUES ('-1560049', 'Thrall, you escaped!', null, null, null, null, null, null, null, null, '0', '0', '0', '0', 'taretha SAY_TA_ESCAPED');
INSERT INTO `script_texts` VALUES ('-1564000', 'You will die in the name of Lady Vashj!', null, null, null, null, null, null, null, null, '11450', '1', '0', '0', 'SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1564001', 'Stick around!', null, null, null, null, null, null, null, null, '11451', '1', '0', '0', 'SAY_NEEDLE1');
INSERT INTO `script_texts` VALUES ('-1564002', 'I\'ll deal with you later!', null, null, null, null, null, null, null, null, '11452', '1', '0', '0', 'SAY_NEEDLE2');
INSERT INTO `script_texts` VALUES ('-1564003', 'Your success was short lived!', null, null, null, null, null, null, null, null, '11455', '1', '0', '0', 'SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564004', 'Time for you to go!', null, null, null, null, null, null, null, null, '11456', '1', '0', '0', 'SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564005', 'Bel\'anen dal\'lorei!', null, null, null, null, null, null, null, null, '11453', '1', '0', '0', 'SAY_SPECIAL1');
INSERT INTO `script_texts` VALUES ('-1564006', 'Blood will flow!', null, null, null, null, null, null, null, null, '11454', '1', '0', '0', 'SAY_SPECIAL2');
INSERT INTO `script_texts` VALUES ('-1564007', 'Bal\'amer ch\'itah!', null, null, null, null, null, null, null, null, '11457', '1', '0', '0', 'SAY_ENRAGE1');
INSERT INTO `script_texts` VALUES ('-1564008', 'My patience has ran out! Die, DIE!', null, null, null, null, null, null, null, null, '11458', '1', '0', '0', 'SAY_ENRAGE2');
INSERT INTO `script_texts` VALUES ('-1564009', 'Lord Illidan will... crush you.', null, null, null, null, null, null, null, null, '11459', '1', '0', '0', 'SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1564010', '%s acquires a new target!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'supremus EMOTE_NEW_TARGET');
INSERT INTO `script_texts` VALUES ('-1564011', '%s punches the ground in anger!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'supremus EMOTE_PUNCH_GROUND');
INSERT INTO `script_texts` VALUES ('-1564012', 'The ground begins to crack open!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'supremus EMOTE_GROUND_CRACK');
INSERT INTO `script_texts` VALUES ('-1564013', 'No! Not yet...', null, null, null, null, null, null, null, null, '11385', '1', '0', '0', 'akama shade SAY_LOW_HEALTH');
INSERT INTO `script_texts` VALUES ('-1564014', 'I will not last much longer...', null, null, null, null, null, null, null, null, '11386', '1', '0', '0', 'akama shade SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1564015', 'Come out from the shadows! I\'ve returned to lead you against our true enemy! Shed your chains and raise your weapons against your Illidari masters!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'akama shade SAY_FREE');
INSERT INTO `script_texts` VALUES ('-1564016', 'Hail our leader! Hail Akama!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'akama shade broken SAY_BROKEN_FREE_01');
INSERT INTO `script_texts` VALUES ('-1564017', 'Hail Akama!', null, null, null, null, null, null, null, null, '0', '1', '0', '0', 'akama shade broken SAY_BROKEN_FREE_02');
INSERT INTO `script_texts` VALUES ('-1564018', 'You play, you pay.', null, null, null, null, null, null, null, null, '11501', '1', '0', '0', 'shahraz SAY_TAUNT1');
INSERT INTO `script_texts` VALUES ('-1564019', 'I\'m not impressed.', null, null, null, null, null, null, null, null, '11502', '1', '0', '0', 'shahraz SAY_TAUNT2');
INSERT INTO `script_texts` VALUES ('-1564020', 'Enjoying yourselves?', null, null, null, null, null, null, null, null, '11503', '1', '0', '0', 'shahraz SAY_TAUNT3');
INSERT INTO `script_texts` VALUES ('-1564021', 'So... business or pleasure?', null, null, null, null, null, null, null, null, '11504', '1', '0', '0', 'shahraz SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1564022', 'You seem a little tense.', null, null, null, null, null, null, null, null, '11505', '1', '0', '0', 'shahraz SAY_SPELL1');
INSERT INTO `script_texts` VALUES ('-1564023', 'Don\'t be shy.', null, null, null, null, null, null, null, null, '11506', '1', '0', '0', 'shahraz SAY_SPELL2');
INSERT INTO `script_texts` VALUES ('-1564024', 'I\'m all... yours.', null, null, null, null, null, null, null, null, '11507', '1', '0', '0', 'shahraz SAY_SPELL3');
INSERT INTO `script_texts` VALUES ('-1564025', 'Easy come, easy go.', null, null, null, null, null, null, null, null, '11508', '1', '0', '0', 'shahraz SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564026', 'So much for a happy ending.', null, null, null, null, null, null, null, null, '11509', '1', '0', '0', 'shahraz SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564027', 'Stop toying with my emotions!', null, null, null, null, null, null, null, null, '11510', '1', '0', '0', 'shahraz SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1564028', 'I wasn\'t... finished.', null, null, null, null, null, null, null, null, '11511', '1', '0', '0', 'shahraz SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1564029', 'Horde will... crush you.', null, null, null, null, null, null, null, null, '11432', '1', '0', '0', 'bloodboil SOUND_AGGRO');
INSERT INTO `script_texts` VALUES ('-1564030', 'Time to feast!', null, null, null, null, null, null, null, null, '11433', '1', '0', '0', 'bloodboil SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564031', 'More! I want more!', null, null, null, null, null, null, null, null, '11434', '1', '0', '0', 'bloodboil SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564032', 'Drink your blood! Eat your flesh!', null, null, null, null, null, null, null, null, '11435', '1', '0', '0', 'bloodboil SAY_SPECIAL1');
INSERT INTO `script_texts` VALUES ('-1564033', 'I hunger!', null, null, null, null, null, null, null, null, '11436', '1', '0', '0', 'bloodboil SAY_SPECIAL2');
INSERT INTO `script_texts` VALUES ('-1564034', '<babbling>', null, null, null, null, null, null, null, null, '11437', '1', '0', '0', 'bloodboil SAY_ENRAGE1');
INSERT INTO `script_texts` VALUES ('-1564035', 'I\'ll rip the meat from your bones!', null, null, null, null, null, null, null, null, '11438', '1', '0', '0', 'bloodboil SAY_ENRAGE2');
INSERT INTO `script_texts` VALUES ('-1564036', 'Aaaahrg...', null, null, null, null, null, null, null, null, '11439', '1', '0', '0', 'bloodboil SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1564037', 'I was the first, you know. For me, the wheel of death has spun many times. <laughs> So much time has passed. I have a lot of catching up to do...', null, null, null, null, null, null, null, null, '11512', '1', '0', '0', 'teron SAY_INTRO');
INSERT INTO `script_texts` VALUES ('-1564038', 'Vengeance is mine!', null, null, null, null, null, null, null, null, '11513', '1', '0', '0', 'teron SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1564039', 'I have use for you!', null, null, null, null, null, null, null, null, '11514', '1', '0', '0', 'teron SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564040', 'It gets worse...', null, null, null, null, null, null, null, null, '11515', '1', '0', '0', 'teron SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564041', 'What are you afraid of?', null, null, null, null, null, null, null, null, '11517', '1', '0', '0', 'teron SAY_SPELL1');
INSERT INTO `script_texts` VALUES ('-1564042', 'Death... really isn\'t so bad.', null, null, null, null, null, null, null, null, '11516', '1', '0', '0', 'teron SAY_SPELL2');
INSERT INTO `script_texts` VALUES ('-1564043', 'Give in!', null, null, null, null, null, null, null, null, '11518', '1', '0', '0', 'teron SAY_SPECIAL1');
INSERT INTO `script_texts` VALUES ('-1564044', 'I have something for you...', null, null, null, null, null, null, null, null, '11519', '1', '0', '0', 'teron SAY_SPECIAL2');
INSERT INTO `script_texts` VALUES ('-1564045', 'YOU WILL SHOW THE PROPER RESPECT!', null, null, null, null, null, null, null, null, '11520', '1', '0', '0', 'teron SAY_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1564046', 'The wheel...spins...again....', null, null, null, null, null, null, null, null, '11521', '1', '0', '0', 'teron SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1564047', 'Pain and suffering are all that await you!', null, null, null, null, null, null, null, null, '11415', '1', '0', '0', 'essence SUFF_SAY_FREED');
INSERT INTO `script_texts` VALUES ('-1564048', 'Don\'t leave me alone!', null, null, null, null, null, null, null, null, '11416', '1', '0', '0', 'essence SUFF_SAY_AGGRO');
INSERT INTO `script_texts` VALUES ('-1564049', 'Look at what you make me do!', null, null, null, null, null, null, null, null, '11417', '1', '0', '0', 'essence SUFF_SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564050', 'I didn\'t ask for this!', null, null, null, null, null, null, null, null, '11418', '1', '0', '0', 'essence SUFF_SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564051', 'The pain is only beginning!', null, null, null, null, null, null, null, null, '11419', '1', '0', '0', 'essence SUFF_SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1564052', 'I don\'t want to go back!', null, null, null, null, null, null, null, null, '11420', '1', '0', '0', 'essence SUFF_SAY_RECAP');
INSERT INTO `script_texts` VALUES ('-1564053', 'Now what do I do?', null, null, null, null, null, null, null, null, '11421', '1', '0', '0', 'essence SUFF_SAY_AFTER');
INSERT INTO `script_texts` VALUES ('-1564054', '%s becomes enraged!', null, null, null, null, null, null, null, null, '0', '3', '0', '0', 'essence SUFF_EMOTE_ENRAGE');
INSERT INTO `script_texts` VALUES ('-1564055', 'You can have anything you desire... for a price.', null, null, null, null, null, null, null, null, '11408', '1', '0', '0', 'essence DESI_SAY_FREED');
INSERT INTO `script_texts` VALUES ('-1564056', 'Fulfilment is at hand!', null, null, null, null, null, null, null, null, '11409', '1', '0', '0', 'essence DESI_SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564057', 'Yes... you\'ll stay with us now...', null, null, null, null, null, null, null, null, '11410', '1', '0', '0', 'essence DESI_SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564058', 'Your reach exceeds your grasp.', null, null, null, null, null, null, null, null, '11412', '1', '0', '0', 'essence DESI_SAY_SLAY3');
INSERT INTO `script_texts` VALUES ('-1564059', 'Be careful what you wish for...', null, null, null, null, null, null, null, null, '11411', '1', '0', '0', 'essence DESI_SAY_SPEC');
INSERT INTO `script_texts` VALUES ('-1564060', 'I\'ll be waiting...', null, null, null, null, null, null, null, null, '11413', '1', '0', '0', 'essence DESI_SAY_RECAP');
INSERT INTO `script_texts` VALUES ('-1564061', 'I won\'t be far...', null, null, null, null, null, null, null, null, '11414', '1', '0', '0', 'essence DESI_SAY_AFTER');
INSERT INTO `script_texts` VALUES ('-1564062', 'Beware: I live!', null, null, null, null, null, null, null, null, '11399', '1', '0', '0', 'essence ANGER_SAY_FREED');
INSERT INTO `script_texts` VALUES ('-1564063', 'So... foolish.', null, null, null, null, null, null, null, null, '11400', '1', '0', '0', 'essence ANGER_SAY_FREED2');
INSERT INTO `script_texts` VALUES ('-1564064', '<maniacal cackle>', null, null, null, null, null, null, null, null, '11401', '1', '0', '0', 'essence ANGER_SAY_SLAY1');
INSERT INTO `script_texts` VALUES ('-1564065', 'Enough. No more.', null, null, null, null, null, null, null, null, '11402', '1', '0', '0', 'essence ANGER_SAY_SLAY2');
INSERT INTO `script_texts` VALUES ('-1564066', 'On your knees!', null, null, null, null, null, null, null, null, '11403', '1', '0', '0', 'essence ANGER_SAY_SPEC');
INSERT INTO `script_texts` VALUES ('-1564067', 'Beware, coward.', null, null, null, null, null, null, null, null, '11405', '1', '0', '0', 'essence ANGER_SAY_BEFORE');
INSERT INTO `script_texts` VALUES ('-1564068', 'I won\'t... be... ignored.', null, null, null, null, null, null, null, null, '11404', '1', '0', '0', 'essence ANGER_SAY_DEATH');
INSERT INTO `script_texts` VALUES ('-1564069', 'You wish to test me?', null, null, null, null, null, null, null, null, '11524', '1', '0', '0', 'council vera AGGRO');
